import React, { useState } from 'react';

import PropTypes from 'prop-types';
import {
    Row, Col,
} from 'reactstrap';

import {
    
} from 'components';


async function loginUser(credentials) {
    return fetch('http://localhost:3000/api/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(credentials)
    })
      .then(data => data.json())
   }

export default function Login({ setToken }) {
    const [username, setUserName] = useState();
    const [password, setPassword] = useState();

    const handleSubmit = async e => {
        e.preventDefault();
        const token = await loginUser({
          username,
          password
        });
        console.log(token);
        setToken(token);
      }
        return (
            <div>
                <div className="">
                    <Row>
                        <Col xs={12} md={12}>

        <div className="container-fluid">
            <div className="login-wrapper row">
                <div id="login" className="login loginpage offset-xl-4 offset-lg-3 offset-md-3 offset-0 col-12 col-md-6 col-xl-4">
                    <h1><a href="#!" title="Login Page" tabIndex="-1">&nbsp;</a></h1>
                    
                    <form name="loginform" id="loginform" action="#!" method="post" onSubmit={handleSubmit}>

                        <p>
                            <label htmlFor="user_login">Username<br/>
                                <input type="text" name="un" id="user_name" className="form-control" onChange={e => setUserName(e.target.value)} />
                            </label>
                        </p>
                        <p>
                            <label htmlFor="user_pass">Password<br />
                                <input type="password" name="pwd" id="user_pass" className="input" size="20" /></label>
                        </p>
                        <p className="submit">
                            <input type="button" name="wp-submit" id="wp-submit" className="btn btn-accent btn-block" value="Sign In" onChange={e => setPassword(e.target.value)} />
                        </p>

                    </form>

                    <p id="nav">
                        <a href="#!" title="Password Lost and Found">Forgot password?</a> | <a href="#!" title="Sign Up">Sign Up</a>
                    </p>


                </div>
            </div>
        </div>






                        </Col>

                    </Row>
                </div>
            </div>
        );
}

Login.propTypes = {
    setToken: PropTypes.func.isRequired
  }
