import React from 'react';
import {
    Row, Col, Label, Input,
} from 'reactstrap';

import InputMask from 'react-input-mask';
import axios from 'axios';
import 'react-datepicker/dist/react-datepicker.css';
import DatePicker from 'react-datepicker';
import moment from 'moment';

class AddDevice extends React.Component{
    constructor (props) {
    super(props)
    this.state = {
    //   startDate: moment()
        customer: [
            'cus_id',
            'cus_name',
        ]
    };
    // this.handleChange = this.handleChange.bind(this);
  }

//   handleChange(date) {
//     this.setState({
//       startDate: date
//     });
//   }
    async componentDidMount(){
        const customers= await axios.get(`http://localhost:3000/admin/showcustomer`);
        this.setState({ customer: customers });
        console.log(customers);
    }
    render(){

        return (
            <div>
                <div className="content">
                    <Row>
                        <Col xs={12} md={12}>

                    <div className="page-title">
                        <div className="float-left">
                            <h1 className="title">Add Device</h1>
                        </div>
                    </div>


                            


                    <div className="row margin-0">
                        <div className="col-12">
                            <section className="box ">
                                <header className="panel_header">
                                    <h2 className="title float-left">Basic Info</h2>
                                    
                                </header>
                                <div className="content-body">
                                    <div className="row">
                                        <div className="col-12 col-sm-12 col-md-10 col-lg-10 col-xl-8">

                                            <form method="POST" action="http://localhost:3000/admindevice/create">
                                                <div className="form-row">
                                                    <div className="form-group col-md-12">
                                                        <label htmlFor="inputname4">Device Name</label>
                                                        <input name="device_name" type="text" className="form-control" id="inputname4" placeholder="" />
                                                    </div>
                                                    {/* <div className="form-group col-md-12">
                                                        <label>Device Start date</label>
                                                        <div className="controls">
                                                            <DatePicker selected={this.state.startDate} onChange={this.handleChange} />
                                                        </div>
                                                   </div> */}

                                                    <div className="form-group col-md-12">
                                                        <label htmlFor="inputname45">Assign Customer</label>
                                                        <input type="text" name="customer" className="form-control" id="inputname45" placeholder="aaa" value="adf" />
                                                    </div>


                                                </div>
                                                <button type="submit" className="btn btn-primary">Save</button>
                                            </form>

                                        </div>
                                    </div>

                                </div>
                            </section></div>



                    </div>






                                
                        </Col>

                    </Row>
                </div>
            </div>
        );
    }
}

export default AddDevice;
