var playlistCharts = {
  data: function data(canvas) {
    var ctx = canvas.getContext("2d");
    var gradientFill = ctx.createLinearGradient(0, 170, 0, 50);
    gradientFill.addColorStop(0, primaryColor);
    gradientFill.addColorStop(1, primaryColor);
    return {
      labels: ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"],
      datasets: [{
        label: "Progress",
        backgroundColor: gradientFill,
        borderColor: primaryColor,
        pointBorderColor: "#FFF",
        pointBackgroundColor: primaryColor,
        pointBorderWidth: 2,
        pointHoverRadius: 4,
        pointHoverBorderWidth: 1,
        pointRadius: 4,
        fill: true,
        borderWidth: 1,
        data: [50, 39, 66, 76, 23, 55, 80, 45, 68, 80, 43, 55]
      }]
    };
  },
  options: {
    maintainAspectRatio: false,
    legend: {
      display: false
    },
    tooltips: {
      bodySpacing: 4,
      mode: "nearest",
      intersect: 0,
      position: "nearest",
      xPadding: 10,
      yPadding: 10,
      caretPadding: 10
    },
    responsive: 1,
    scales: {
      yAxes: [{
        display: 0,
        ticks: {
          display: false
        },
        gridLines: {
          color: "rgba(0, 0, 0, 0.01)",
          zeroLineColor: "transparent",
          drawTicks: false,
          display: false,
          drawBorder: false
        }
      }],
      xAxes: [{
        categoryPercentage: 0.6,
        barPercentage: 0.5,
        maxBarThickness: 8,
        display: 0,
        ticks: {
          display: false
        },
        gridLines: {
          color: "rgba(0, 0, 0, 0.01)",
          zeroLineColor: "transparent",
          drawTicks: false,
          display: false,
          drawBorder: false
        }
      }]
    },
    layout: {
